    const [ connect, setConnect] = useState(false);
    const [ pageCourante, setPageCourante] = useState('login');


    function getConnect() {
        setPageCourante("message_page"); 
        setConnect(true); 
    }
    
    return connect ? <HomePage/> : <LoginPage/>;

};
